import React, { useEffect } from "react";
import NavBar from "./NavBar";

function CodingScreen() {
  useEffect(() => {
    const script = document.createElement("script");
    script.src = "https://cdnjs.cloudflare.com/ajax/libs/ace/1.23.0/ace.js";
    script.integrity =
      "sha512-+jESSLfWnLFLB0xrEnlj3yESiiuuuOnVDcq+HXImWWCB5CBqAnVsmtIZ5GtrNMH0C6gPZoj6n14zJ0iwGYoswQ==";
    script.crossOrigin = "anonymous";
    script.referrerPolicy = "no-referrer";
    script.async = true;
    document.body.appendChild(script);

    script.onload = () => {
      const editor = ace.edit("editor");
      editor.setTheme("ace/theme/monokai");
      editor.session.setMode("ace/mode/python");
    };
  }, []);

  return (
    <div className="codingScreenBody">
      <NavBar />
      <div className="header">
        <h1>Python Loops</h1>
      </div>
      <div className="container">
        <div className="question"></div>

        <div className="code-editor" id="editor"></div>
      </div>
    </div>
  );
}

export default CodingScreen;
