import React from "react";
import NavBar from "./NavBar";
import NavbarCoding from "./NavBarCoding";
import AceEditor from 'react-ace';
import 'brace/mode/javascript';
import 'brace/theme/tomorrow';
import { useState } from "react";
import axios from "axios";

function CodingScreen() {

    const [userCode, setUserCode] = useState(``);

    // State variable to set editors default language
    const [userLang, setUserLang] = useState("python");

    // State variable to set editors default theme
    const [userTheme, setUserTheme] = useState("ace_light");

    // State variable to set editors default font size
    const [fontSize, setFontSize] = useState(20);

    // State variable to set users input
    const [userInput, setUserInput] = useState("");

    // State variable to set users output
    const [userOutput, setUserOutput] = useState("");

    // Loading state variable to show spinner
    // while fetching data
    const [loading, setLoading] = useState(false);

    const options = {
        fontSize: fontSize
    }

    function handleChange(value) {
        setUserCode(value)
    }


    function compile() {
        setLoading(true);
        if (userCode === ``) {
            return
        }

        // Post request to compile endpoint
        axios.post(`http://localhost:3000/compile`, {
            code: userCode,
            language: userLang,
            input: userInput
        }).then((res) => {
            setUserOutput(res.data.output);
        }).then(() => {
            setLoading(false);
        })
    }

    // Function to clear the output screen
    function clearOutput() {
        setUserOutput("");
    }

    return (
        <div className="codingScreenBody">
            <NavBar />
            <div className="header">
                <h1>Python Loops</h1>
            </div>
            <div className="container">
                <div className="question">
                    <h2>What is your name</h2>
                </div>
                <NavbarCoding
                userLang={userLang} setUserLang={setUserLang}
                userTheme={userTheme} setUserTheme={setUserTheme}
                fontSize={fontSize} setFontSize={setFontSize}
            />

                <div className="editor-output-container">
                    <div className="code-editor" id="editor">
                        <AceEditor
                            mode={userLang}
                            theme={userTheme}
                            name="my-editor"
                            width="100%"
                            height="800px"
                            editorProps={{ $blockScrolling: true }}
                            onChange={handleChange}
                        />
                    </div>
                    <div className="input-output-area">
                        <h4>input:</h4>
                        <div className="input-box">
                            <textarea id="code-input"
                                onChange={(e) => setUserInput(e.target.value)}>
                            </textarea>

                        </div>
                        <h4>output:</h4>
                        {loading ? (
                            <div className="spinner-box">
                                <img src="" alt="loading..." />
                            </div>
                        ) : (
                            <div className="output-box">
                                <pre>{userOutput}</pre>

                            </div>
                        )}
                        <div>

                        </div>
                    </div>
                </div>

                <div className="button-container">
                    <button type="submit" onClick={() => compile()}>Run</button>
                    <button onClick={() => { clearOutput() }}>Clear</button>

                </div>
            </div>
        </div>
    );
}

export default CodingScreen;
