import React, { useState } from "react"
import Exercise from "./Exercise"
import NavBar from "./NavBar"
import axios from "axios"
import { useNavigate } from "react-router-dom";
import CodingScreen from "./CodingScreen";



function HomePage() {
    const [exerciseData, setExerciseData] = useState(null);
    const navigate = useNavigate();

    const handleExerciseButtonClick = (exerciseType) => {
        console.log('Button clicked with exerciseType:', exerciseType);

        // Make a network request to the backend with exerciseType

        axios.get('http://localhost:3000/api/exerciseTemplate', {
            params: {
                exerciseType: exerciseType
            }
        })
            //   .get(`http://localhost:3000/api/exerciseTemplate?exerciseType=${exerciseType}`)
            .then((response) => {

                console.log("exerciseData variables:", response.data); // Check the response data in the browser console
                setExerciseData(response.data);
                // navigate("/codingscreen");
                navigate("/codingscreen", { state: { exerciseData: response.data } });
                
            })
            .catch((error) => {
                console.error("Error fetching data from the server:", error);
            });
    };




    return (
        <div className="HomePageBody">
            <NavBar />
            <div className="homeContainer">
                <Exercise
                    className="question"
                    title="Loops"
                    para="Level-Easy"
                    onExerciseButtonClick={() => handleExerciseButtonClick("loops")}



                />

                <Exercise
                    className="question"
                    title="Question 2"
                    para="Level-Easy"



                />

                <Exercise
                    className="question"
                    title="Question 3"
                    para="Level-Easy"



                />
                <Exercise
                    className="question"
                    title="Question 4"
                    para="Level-Easy"



                />


                <Exercise
                    className="question"
                    title="Question 5"
                    para="Level-Easy"



                />

                <Exercise
                    className="question"
                    title="Question 6"
                    para="Level-Easy"



                />

                <Exercise
                    className="question"
                    title="Question 7"
                    para="Level-Easy"



                />

                <Exercise
                    className="question"
                    title="Question 8"
                    para="Level-Easy"

                />

                <Exercise
                    className="question"
                    title="Question 9"
                    para="Level-Easy"



                />

                <Exercise
                    className="question"
                    title="Question 10"
                    para="Level-Easy"



                />

                {/* {exerciseData && <CodingScreen exerciseData={exerciseData} />} */}

            </div>
         
        </div>
    )


}

export default HomePage
