import React, { useState } from "react"
import InputArea from "./InputArea"
import Heading from "./Heading"


function Login(){

    const [user,setUser] = useState("")

    function handleClick(event){
      const name = event.target.name
      const newValue = event.target.value
      setUser((prevValue) => ({
        ...prevValue,
        [name] : newValue

      }))
    }


    return(
    <div className="signupFrm">
    <form className="form">
    <Heading className="title" headingText="Login" />

      <div className="inputContainer">
        <InputArea type="text" className="input" placeholder="Username" labelClassName="label" labelValue="Username" />
      </div>

      <div className="inputContainer">
        <InputArea type="text" className="input" placeholder="Password" labelClassName="label" labelValue="Password" />
      </div>

    


      <InputArea type="submit" className="submitBtn" value="Login" onClick={handleClick}/ >
    </form>
  </div>
        
    )
}

export default Login
