import React, { useState } from "react"
import InputArea from "./InputArea"
import Heading from "./Heading"
import axios from "axios";
import {Link, useNavigate} from "react-router-dom"
function Login() {

  const navigate = useNavigate()

  const [user, setUser] = useState({
    username: "",
    password: ""
  })

  function handleChange(event) {
    const { name, value } = event.target
    setUser((prevValue) => ({
      ...prevValue,
      [name]: value

    }))
  }


  function handleClick(event) {
    event.preventDefault();



    axios
      .post("http://localhost:3000/login", user)
      .then((response) => {
        if(response.data="Valid users"){
          navigate("/homepage")
        }
        else if(response.data="Invalid users"){
          alert("Invalid users")
        }
      
        
        console.log(response.data.message);
        // Handle successful login, such as redirecting to a dashboard page
      })
      .catch((error) => {
        console.log("Error logging in:", error);
        // Handle login error, such as displaying an error message
      });
  }




  return (
    <div className="signupFrm">
      <form className="form">
        <Heading className="title" headingText="Login" />

        <div className="inputContainer">
          <InputArea
            onChange={handleChange}
            name="username"
            value={user.username}
            type="text"
            className="input"
            placeholder="Username"
            labelClassName="label"
            labelValue="Username" />
        </div>

        <div className="inputContainer">
          <InputArea
            onChange={handleChange}
            name="password"
            value={user.password}
            type="password"
            className="input"
            placeholder="Password"
            labelClassName="label"
            labelValue="Password" />
        </div>



        <Link to="/homepage">
        <input type="submit" className="submitBtn" value="Login" onClick={handleClick} />
        </Link>
      </form>
    </div>

  )
}

export default Login
