import React, { useEffect } from "react";
import NavBar from "./NavBar";
import NavBarCoding from "./NavBarCoding";
import NextSampleButton from "./NextSampleButton"
import exerciseTemplates from "./Templates";
import AceEditor from 'react-ace';
import 'brace/mode/javascript';
import 'brace/theme/tomorrow';
import { useState } from "react";
import axios from "axios";

function CodingScreen() {

    const [userCode, setUserCode] = useState(``);

    // State variable to set editors default language
    const [userLang, setUserLang] = useState("python");

    // State variable to set editors default theme
    const [userTheme, setUserTheme] = useState("github");

    // State variable to set editors default font size
    const [fontSize, setFontSize] = useState(20);

    // State variable to set users input
    const [userInput, setUserInput] = useState("");

    // State variable to set users output
    const [userOutput, setUserOutput] = useState("");

    // Loading state variable to show spinner
    // while fetching data
    const [loading, setLoading] = useState(false);




    function handleChange(value) {
        setUserCode(value)
    }


    function compile() {
        setLoading(true);
        if (userCode === ``) {
            return
        }

        // Post request to compile endpoint
        axios.post(`http://localhost:3000/compile`, {
            code: userCode,
            language: userLang,
            input: userInput
        }).then((res) => {
            setUserOutput(res.data.output);

        }).then(() => {
            setLoading(false);
        })
    }

    // Function to clear the output screen
    function clearOutput() {
        setUserOutput("");
    }

    useEffect(() => {
        generateRandomExercise();
      },[]);
    
    

    const [currentExercise, setCurrentExercise] = useState("");
    const generateRandomExercise = () => {
       
    
        const number = Math.floor(Math.random() * 100) + 1; // Generate a random number
        const template =
          exerciseTemplates[Math.floor(Math.random() * exerciseTemplates.length)]; // Select a random exercise template
        const exercise = template.replace("{number}", number); // Substitute the number into the template
    
        setCurrentExercise(exercise);
      };            
    
    const handleClick= ()=>{
        generateRandomExercise()

    }

    return (
        <div className="codingScreenBody">
            <NavBar />
            <div className="header">
                <h1>Python Loops</h1>
            </div>
            <div className="container">
                <div className="question">
                    <h2>{currentExercise}</h2>
                </div>
                <NavBarCoding
                    userLang={userLang} setUserLang={setUserLang}
                    userTheme={userTheme} setUserTheme={setUserTheme}
                    fontSize={fontSize} setFontSize={setFontSize}
                />

                <div className="editor-output-container">
                    <div className="code-editor" id="editor">
                        <AceEditor
                            mode={userLang}
                            theme={userTheme}
                            name="my-editor"
                            width="100%"
                            height="800px"
                            fontSize={`${fontSize}px`}
                            editorProps={{ $blockScrolling: true }}
                            showPrintMargin={true}
                            showGutter={true}
                            highlightActiveLine={true}
                            onChange={handleChange}
                            setOptions={{
                                enableBasicAutocompletion: true,
                                enableLiveAutocompletion: true,
                                enableSnippets: true,
                                showLineNumbers: true,
                                tabSize: 2,
                            }}
                        />
                    </div>
                    <div className="input-output-area">
                        <h4>input:</h4>
                        <div className="input-box">
                            <textarea id="code-input"
                                onChange={(e) => setUserInput(e.target.value)}>
                            </textarea>

                        </div>
                        <h4>output:</h4>
                        {loading ? (
                            <div className="spinner-box">
                                <img src="./images/loading.gif" alt="loading..." />
                            </div>
                        ) : (
                            <div className="output-box">
                                <pre style={{ color: "white" }}>{userOutput}</pre>

                            </div>
                        )}
                        <div>

                        </div>
                    </div>
                </div>

                <div className="button-container">
                    <NextSampleButton 
                        classNameNext="nextButton"
                        classNameSample="sampleSolutionButton"
                        onClickQuestion={generateRandomQuestion}
                        onClickSmaple={handleGenerateSampleSolution}
                    />

                    <button type="submit" className="codeButton" onClick={() => compile()}>Run</button>
                    <button className="clearButton" onClick={() => { clearOutput() }}>Clear</button>

                </div>
            </div>
        </div>
    );
}

export default CodingScreen;
