import React, { useEffect } from "react";
import { useParams } from "react-router-dom";
import NavBar from "./NavBar";
import NavBarCoding from "./NavBarCoding";
import NextSampleButton from "./NextSampleButton";
import AceEditor from 'react-ace';
import 'brace/mode/javascript';
import 'brace/theme/tomorrow';
import { useState } from "react";
import loopExerciseTemplate from "./Templates";
import axios from "axios";

function CodingScreen() {
    // const { exerciseType } = useParams();

    const [userCode, setUserCode] = useState(``);

    const [userLang, setUserLang] = useState("python");

    const [userTheme, setUserTheme] = useState("github");

    const [fontSize, setFontSize] = useState(20);

    const [userInput, setUserInput] = useState("");

    const [userOutput, setUserOutput] = useState("");

    const [loading, setLoading] = useState(false);




    function handleChange(value) {
        setUserCode(value)
    }


    function compile() {
        setLoading(true);
        if (userCode === ``) {
            return
        }

        // Post request to compile endpoint
        axios.post(`http://localhost:3000/compile`, {
            code: userCode,
            language: userLang,
            input: userInput
        }).then((res) => {
            setUserOutput(res.data.output);

        }).then(() => {
            setLoading(false);
        })
    }

    // Function to clear the output screen
    function clearOutput() {
        setUserOutput("");
    }

    // const [randomQuestionData, setRandomQuestionData] = useState({
    //     question: "",
    //     key: null,
    //     usedVariables: {},
    // });

    // const [sampleSolution, setSampleSolution] = useState("");
