        // Fetch loopExerciseTemplate and variable data from the backend (MongoDB)
    //     axios.get("http://localhost:3000/api/questions-and-variables")
    //     .then((response) => {
    //         const { questions, variables } = response.data;
    //         setQuestions(questions);
    //         setVariables(variables);
    //         generateRandomQuestion(questions, variables);
    //     })
    //     .catch((error) => {
    //         console.error("Error fetching data from the server:", error);
    //     });


    //     axios.get("http://localhost:5000/api/solutions")
    //         .then((response) => {
    //             setSolutions(response.data);
    //             handleGenerateSampleSolution();
    //         })
    //         .catch((error) => {
    //             console.error("Error fetching solution data from the server:", error);
    //         });

       
    // }, [exerciseType]);

    // const getRandomItem = (array) => array[Math.floor(Math.random() * array.length)];

    // const generateRandomQuestion = () => {
    //     const randomTemplate = getRandomItem(loopExerciseTemplate.questions);
    //     const usedVariables = {};

    //     let question = randomTemplate.text.replace(/{(\w+)}/g, (match, variable) => {
    //         if (variables.hasOwnProperty(variable)) {
    //             let variableValue = getRandomItem(variables[variable]);
    //             usedVariables[variable] = variableValue;
    //             return variableValue;
    //         }
    //         return match;
    //     });

    //     setRandomQuestionData({ question, usedVariables, key: randomTemplate.key });
    // };
    

    // const handleGenerateSampleSolution = () => {
    //     const { key, usedVariables } = randomQuestionData;
    //     const matchingSolution = solutions.find((solution) => solution.key === key);

    //     if (matchingSolution) {
    //         let solutionCode = matchingSolution.template.replace(/{(\w+)}/g, (match, variable) => {
    //             if (usedVariables.hasOwnProperty(variable)) {
    //                 return usedVariables[variable];
    //             }
    //             return match;
    //         });

    //         setSampleSolution(solutionCode);
    //     } else {
    //         setSampleSolution("Sample solution not available for this question.");
    //     }
    // }

  const [randomQuestionData, setRandomQuestionData] = useState(null);
  const [sampleSolution, setSampleSolution] = useState("");

  useEffect(() => {
    // Fetch loopExerciseTemplate data
    setRandomQuestionData(generateRandomQuestion());
    handleGenerateSampleSolution();
  }, []); // Run this effect only once when the component mounts

  const getRandomItem = (array) => array[Math.floor(Math.random() * array.length)];

  const generateRandomQuestion = () => {
    const randomTemplate = getRandomItem(loopExerciseTemplate.questions);
    const usedVariables = {};
  
    let question = randomTemplate.text.replace(/{(\w+)}/g, (match, variable) => {
      if (loopExerciseTemplate.variables?.hasOwnProperty(variable)) {
        let variableValue = getRandomItem(loopExerciseTemplate.variables[variable]);
        usedVariables[variable] = variableValue;
        return variableValue;
      }
      return match;
    });
  
    return { question, usedVariables, key: randomTemplate.key };
  };
  

  const handleGenerateSampleSolution = () => {
    if (questionData && questionData.key) {
        const { key, usedVariables } = questionData;
        const matchingSolution = loopExerciseTemplate.solutions.find((solution) => solution.key === key);
    
        if (matchingSolution) {
          let solutionCode = matchingSolution.template.replace(/{(\w+)}/g, (match, variable) => {
            if (usedVariables.hasOwnProperty(variable)) {
              return usedVariables[variable];
            }
            return match;
          });
      setSampleSolution(solutionCode);
    } else {
      setSampleSolution("Sample solution not available for this question.");
    }
  }};

    return (
        <div className="codingScreenBody">
            <NavBar />
            <div className="header">
                <h1>Python Loops</h1>
            </div>
            <div className="container">
                <div className="question">
                    <h2>{randomQuestionData}</h2>
                </div>
                <NavBarCoding
                    userLang={userLang} setUserLang={setUserLang}
                    userTheme={userTheme} setUserTheme={setUserTheme}
                    fontSize={fontSize} setFontSize={setFontSize}
                />

                <div className="editor-output-container">
                    <div className="code-editor" id="editor">
                        <AceEditor
                            mode={userLang}
                            theme={userTheme}
                            name="my-editor"
                            width="100%"
                            height="800px"
                            fontSize={`${fontSize}px`}
                            editorProps={{ $blockScrolling: true }}
                            showPrintMargin={true}
                            showGutter={true}
                            highlightActiveLine={true}
                            onChange={handleChange}
                            setOptions={{
                                enableBasicAutocompletion: true,
                                enableLiveAutocompletion: true,
                                enableSnippets: true,
                                showLineNumbers: true,
                                tabSize: 2,
                            }}
                        />
                    </div>
                    <div className="input-output-area">
                        <h4>input:</h4>
                        <div className="input-box">
                            <textarea id="code-input"
                                onChange={(e) => setUserInput(e.target.value)}>
                            </textarea>

                        </div>
                        <h4>output:</h4>
                        {loading ? (
                            <div className="spinner-box">
                                <img src="./images/loading.gif" alt="loading..." />
                            </div>
                        ) : (
                            <div className="output-box">
                                <pre style={{ color: "white" }}>{userOutput}</pre>

                            </div>
                        )}
                        <div>

                        </div>
                    </div>
                </div>

                <div className="button-container">
                    <NextSampleButton
                        classNameNext="nextButton"
                        classNameSample="sampleSolutionButton"
                        onClickQuestion={generateRandomQuestion}
                        onClickSmaple={handleGenerateSampleSolution}
                    />

                    <button type="submit" className="codeButton" onClick={() => compile()}>Run</button>
                    <button className="clearButton" onClick={() => { clearOutput() }}>Clear</button>

                </div>
            </div>
        </div>
    );
}

export default CodingScreen;
